import React,{ useState} from 'react';
import { useRef } from 'react';
import {useEffect} from 'react';
import "./Form.css";

const Form= () => {
    let [formData,setFormData] = useState([]);
    const formRef=useRef()

    useEffect(() => {
      console.log(formData.length);
    //   for(let i=0; i<formRef.current.length-2; i++){
    //     formRef.current[i].classList.remove("border-alert");

    // }
    
    }, [formData]);
    

    const onFormSubmit = (event) =>{
        event.preventDefault();
        setFormData(formData.splice(0,formData.length));
        let dic={}
        for(let i=0; i<formRef.current.length-2; i++){
            let data = formRef.current[i]
            if(data.value===""){
                data.classList.add("border-alert");
                return
            }
            // formData.push({data.id : data.value});
            dic[data.id]=data.value;
        }
        console.log(dic);
            
    }

    return (<>
            <div className='container'>
                <div className='form-container'>
                    <form className='form' ref={formRef} onSubmit={(e) =>{onFormSubmit(e);}}>
                        <h1>Employee registration from</h1>
                            
                        <div className='form-item'>
                            <label >First-name: </label>
                            <input type="text" className='first-name' id="first-name" />
                        </div>
                        <div className='form-item'>
                            <label >Last-name: </label>
                            <input type="text" className='last-name' id="last-name" />
                        </div>
                        <div className='form-item'>
                            <label >Email: </label>
                            <input type="email" className='email' id="email" />
                        </div>
                        <div className='form-item'>
                            <label >Contact Number: </label>
                            <input type="text" className='contact' id="contact" />
                        </div>
                        <div className='form-item'>
                            <label >Address: </label>
                            <input type="text" className='address' id="address" />
                        </div>
                        <div className='form-item'>
                        <label >Applying for position: </label>
                            <select name='position' id='position'>
                                <option value="developer">Developer</option>
                                <option value="tester">Tester</option>
                                <option value="hr">HR</option>
                            </select>
                            <input type="file" id="" />
                        </div>
                        <button className='submit-btn' type="submit">Submit</button>
                    </form>
                    
                </div>
                
            </div>
        </>
    );
}

export default Form;
