    let [stat,setStat]=useState(0);
    let [data,setData]=useState();
    const getDetails = (details) =>{
        setStat(Object.keys(details).length);
        setData({
            'Name':details['first-name']+" "+details['first-name'],

        })
        // console.log(details)
    }
    return (
        <div>
            {stat===7?<Submitted/>:<Form passDetails={getDetails}/>}
        </div>
    );
}

export default App;
