import React,{ useState} from 'react';
import { useRef } from 'react';
import "./Form.css";

const Form= () => {
    let [formData,setFormData] = useState([]);
    let [formStatus,setFormStatus] = useState(0);
    const formRef=useRef()
    

    const onFormSubmit = (event) =>{
        event.preventDefault();
        setFormStatus(0);
        setFormData(formData.splice(0,formData.length));
        let dic={}
        for(let i=0; i<formRef.current.length-1; i++){
            let data = formRef.current[i]
            if(data.value===""){
                data.classList.add("border-alert");
                setFormStatus(formStatus++);
            }else{
                data.classList.remove("border-alert");

            }
            // formData.push({data.id : data.value});
            dic[data.id]=data.value;
        }
        formData.push(dic);
        console.log(formData[0]);
        console.log(formStatus);
    }
    if(formStatus===0){
        return <div>
            <h1>Form submitted!!</h1>
        </div>
   
        
    }
    else{
    }
}

export default Form;
